import axios from 'axios';
import moment from 'moment/moment';
import React, { useEffect, useState } from 'react'
import { Form, Table , Button, Card, ListGroup, ListGroupItem } from 'react-bootstrap';
import * as Icon from 'react-bootstrap-icons';
import Modal from 'react-bootstrap/Modal';
import "./css.css";

const URL_DB = "http://localhost:3002/rdv";
const dateDebutCalendrier = new Date("December 28, 2000");
const dateFinCalendrier = new Date("January 30, 2040");

export default function Calendrier() {

    const [annees, setAnnees] = useState();
    const [mois, setMois] = useState();
    const [jour, setJour] = useState();

    const [calendrier, setCalendrier] = useState([]);

   
    const [dateChoisi, setDateChoisi] = useState();



    const [years, setYears] = useState();
    const [month, setMonth] = useState();
    const [day, setDay] = useState();

    const [calendar, setCalendar] = useState([]);

    const [currentMonth , setCurrentMonth] = useState([]); 
    const [selectedDay, setSelectedDay] = useState();


    const [rdv , setRdv] = useState({
        titre : "",
        commentaire :"",
    });

    const [allRDV, setAllRdv] = useState([]);
    const [rdvDuJourChoisi, setRdvDuJourChoisi] = useState([]);

    const options = { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' };
    


    const monthName = moment.months();

    const dayName = moment.weekdays();



    const onChange= (e) =>{
        const {name , value}= e.target ;
        setRdv(prevForm =>{ 
            return  {...prevForm , [name]: value} 
        })
    }
   /*  const prevMonth = () => {
        console.log(mois-1); // Mois précédent 
        if(mois-1 == 0){
            setMois(12);
            setAnnees(annees-1);
        }   
        else setMois(mois-1);
    }
    const nextMonth = () => {
        console.log(mois+1); // Mois suivant
        if(mois+1 == 13){
            setMois(1);
            setAnnees(annees+1);
        }
        else setMois(mois+1);
    }
    const nextYear = () => {
        console.log(annees+1); // Année suivante
        setAnnees(annees+1);
    }
    const prevYear = () => {
        console.log(annees-1); // Année précedente
        setAnnees(annees-1);
    }
    const dayClick = (a) => {
        console.log(a);
        setDateChoisi(a.toLocaleDateString("en-US",options));
    }
    const supprimerRdv = (e, rdv) => {
        e.preventDefault();
        axios.delete(URL_DB + `/${rdv.id}`).then(res=> console.log(res));
    }
    const prendreRdv = (e) =>{
        e.preventDefault();
        console.log(dateChoisi);
        console.log(rdv);
        let obj = {
            titre : rdv.titre,
            commentaire : rdv.commentaire,
            date : dateChoisi
        }
        axios.post(URL_DB, obj).then(res => console.log(res));
    }



    useEffect(() => {
        const result = calendrier.filter(c => (c.getMonth()) === (mois-1) && c.getFullYear() === annees);
        setCurrentMonth(result);
        setDateChoisi();
    }, [annees,jour,mois]);



    useEffect(() => {
        axios.get(URL_DB+`?date=${dateChoisi}`).then(res => setRdvDuJourChoisi(res.data));
    }, [dateChoisi]);


// remplire calendrier
    useEffect(() => {
        setAllRdv([]);
        const date = new Date();
        setMois(date.getMonth()+1);
        setAnnees(date.getFullYear());
        setJour(date.getDate());
        var r = [];
        while (dateDebutCalendrier <= dateFinCalendrier)
        {
            r.push(new Date(dateDebutCalendrier.setDate(dateDebutCalendrier.getDate() + 1)));
        }
        setCalendrier(r);
        setRdvDuJourChoisi([]);
        axios.get(URL_DB).then(res => setAllRdv(res.data));
    }, []);
 */


    //----------------------------------------------------------------------------------------------------------------------------------------------------------------------------------


    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);


    const prevMonth = () => {
        console.log(month-1); // Mois précédent 
        if(month-1 === 0){
            setMonth(12);
            setYears(years-1);
        }   
        else setMonth(month-1);
    }
    const nextMonth = () => {
        console.log(month+1); // Mois suivant
        if(month+1 == 13){
            setMonth(1);
            setYears(years+1);
        }
        else setMonth(month+1);
    }
    const nextYear = () => {
        console.log(years+1); // Année suivante
        setYears(years+1);
    }
    const prevYear = () => {
        console.log(years-1); // Année précedente
        setYears(years-1);
    }
    const dayClick = (a) => {
        console.log(a);
        setSelectedDay(a.toLocaleDateString("en-US",options));
        
    }


    const dayClickColor = (a) => {
       return  (
        <td className='col-md-1 item-tab' key={a} 
        style={{backgroundColor:'red',color:'white'}}>
            
        </td>
      ) 
    }

    const supprimerRdv = (e, rdv) => {
        e.preventDefault();
        axios.delete(URL_DB + `/${rdv.id}`).then(res=> console.log(res));
    }
    const prendreRdv = (e) =>{
        e.preventDefault();
        console.log(selectedDay);
        console.log(rdv);
        let obj = {
            titre : rdv.titre,
            commentaire : rdv.commentaire,
            date : selectedDay
        }
        axios.post(URL_DB, obj).then(res => console.log(res));
    }



    useEffect(() => {
        const result = calendar.filter(c => (c.getMonth()) === (month-1) && c.getFullYear() === years);
        setCurrentMonth(result);
        setSelectedDay();
    }, [years,day,month]);


    // gett
    useEffect(() => {
        axios.get(URL_DB+`?date=${selectedDay}`).then(res => setRdvDuJourChoisi(res.data));
    }, [selectedDay]);


// remplire calendrier
    useEffect(() => {
        setAllRdv([]);
        const date = new Date();
        setMonth(date.getMonth()+1);
        setYears(date.getFullYear());
        setDay(date.getDate());
        var r = [];
        while (dateDebutCalendrier <= dateFinCalendrier)
        {
            r.push(new Date(dateDebutCalendrier.setDate(dateDebutCalendrier.getDate() + 1)));
        }
        setCalendar(r);
        setRdvDuJourChoisi([]);
        axios.get(URL_DB).then(res => setAllRdv(res.data));
    }, []);



    return (
        <div className="container">
            <div className='row text-center mt-5'>
                <div className="col-md-12">

                    <div className="row text-center">
                        <div className="d-flex justify-content-center">
                            <button className='btn btn-outline-secondary prev' onClick={()=> prevMonth()}> <Icon.ArrowLeftSquare/> </button>    
                            <b className='mt-2'>{monthName[month-1]}</b>
                            <button className='btn btn-outline-secondary next' onClick={() => nextMonth()}> <Icon.ArrowRightSquare/> </button>    
                        </div>
                        <div className="mt-2 d-flex justify-content-center">
                            <button className='btn btn-outline-secondary prev' onClick={() => prevYear()}> <Icon.ArrowLeftSquare/> </button>    
                            <b className='mt-2'>{years}</b>
                            <button className='btn btn-outline-secondary next' onClick={() => nextYear()}> <Icon.ArrowRightSquare/> </button>    
                        </div>
                    </div>



                    <br />
                    <Table bordered>
                        <thead>
                            <tr>
                                {currentMonth.length > 0 && currentMonth.slice(0,7).map(a => {
                             
                                    return <th scope="col">{dayName[a.getDay()]}</th>
                                })}
                            </tr>
                        </thead>
                        <tbody className='body-table'>
                                {/* Premiere Semaine */}
                                <tr> 

                           


                                    
                                {currentMonth.length > 0 && currentMonth.slice(0,7).map(a => {
                                    const rdvs = allRDV.filter(rdv => rdv.date === a.toLocaleDateString("en-US",options));
                                    //mettre la case de jour en noire
                                    if(new Date().toLocaleDateString("en-US",options) == a.toLocaleDateString("en-US",options)){
                                        return <td className='col-md-1 item-tab' 
                                                    key={a} 
                                                    style={{backgroundColor:'black',color:'white'}}
                                                    onClick={()=> {
                                                    dayClick(a);
                                                    
                                                }}>
                                                    {a.getDate()}
                                                 </td>
                                    }
                                        
                                    
                                    //griser la case des rdv déja prise
                                    if(rdvs.length>0){
                                        return <td className='col-md-1 item-tab' 
                                                    key={a} 
                                                    style={{backgroundColor:'grey'}}
                                                    onClick={()=> {
                                                    dayClick(a);
                                                    
                                                }}>
                                                    {a.getDate()}
                                                 </td>
                                    }else{
                                        return <td className='col-md-1 item-tab' key={a} onClick={()=> {
                                            dayClick(a);
                                            dayClickColor(a);
                                        }}>{a.getDate()}</td>
                                    }
                                })}
                                </tr>
                                <tr> 
                                 {currentMonth.length > 0 && currentMonth.slice(7,14).map(a => {
                                    const rdvs = allRDV.filter(rdv => rdv.date === a.toLocaleDateString("en-US",options));
                                    if(new Date().toLocaleDateString("en-US",options) === a.toLocaleDateString("en-US",options)){
                                        return <td className='col-md-1 item-tab' 
                                                    key={a} 
                                                    style={{backgroundColor:'black',color:'white'}}
                                                    onClick={()=> {
                                                    dayClick(a);
                                                    
                                                }}>
                                                    {a.getDate()}
                                                 </td>
                                    }
                                    if(rdvs.length>0){
                                        return <td className='col-md-1 item-tab' 
                                                    key={a} 
                                                    style={{backgroundColor:'grey'}}
                                                    onClick={()=> {
                                                    dayClick(a);
                                                    
                                                }}>
                                                    {a.getDate()}
                                                 </td>
                                    }else{
                                        return <td className='col-md-1 item-tab' key={a} onClick={()=> {
                                            dayClick(a);
                                            dayClickColor(a);
                                        }}>{a.getDate()}</td>
                                    }
                                })}
                                </tr>
                                <tr> 
                                {currentMonth.length > 0 && currentMonth.slice(14,21).map(a => {
                                    const rdvs = allRDV.filter(rdv => rdv.date === a.toLocaleDateString("en-US",options));
                                    if(new Date().toLocaleDateString("en-US",options) == a.toLocaleDateString("en-US",options)){
                                        return <td className='col-md-1 item-tab' 
                                                    key={a} 
                                                    style={{backgroundColor:'black',color:'white'}}
                                                    onClick={()=> {
                                                    dayClick(a);
                                                    
                                                }}>
                                                    {a.getDate()}
                                                 </td>
                                    }
                                    if(rdvs.length>0){
                                        return <td className='col-md-1 item-tab' 
                                                    key={a} 
                                                    style={{backgroundColor:'grey'}}
                                                    onClick={()=> {
                                                    dayClick(a);
                                                    
                                                }}>
                                                    {a.getDate()}
                                                 </td>
                                    }else{
                                        return <td className='col-md-1 item-tab' key={a} onClick={()=> {
                                            dayClick(a);
                                            dayClickColor(a);
                                        }}>{a.getDate()}</td>
                                    }
                                    
                                })}
                                </tr>
                                <tr> 
                                {currentMonth.length > 0 && currentMonth.slice(21,28).map(a => {
                                    const rdvs = allRDV.filter(rdv => rdv.date === a.toLocaleDateString("en-US",options));
                                    if(new Date().toLocaleDateString("en-US",options) == a.toLocaleDateString("en-US",options)){
                                        return <td className='col-md-1 item-tab' 
                                                    key={a} 
                                                    style={{backgroundColor:'black',color:'white'}}
                                                    onClick={()=> {
                                                    dayClick(a);
                                                    
                                                }}>
                                                    {a.getDate()}
                                                 </td>
                                    }
                                    if(rdvs.length>0){
                                        
                                        return <td className='col-md-1 item-tab' 
                                                    key={a} 
                                                    style={{backgroundColor:'grey'}}
                                                    onClick={()=> {
                                                    dayClick(a);
                                                    
                                                }}>
                                                    {a.getDate()}
                                                 </td>
                                    }else{
                                        return <td className='col-md-1 item-tab' key={a} onClick={()=> {
                                            dayClick(a);
                                            dayClickColor(a);
                                        }}>{a.getDate()}</td>
                                    }
                                })}
                                </tr>
                                <tr> 
                                {currentMonth.length > 0 && currentMonth.slice(28,31).map(a => {
                                    const rdvs = allRDV.filter(rdv => rdv.date === a.toLocaleDateString("en-US",options));
                                    if(rdvs.length>0){
                                        
                                        return <td className='col-md-1 item-tab' 
                                                    key={a} 
                                                    style={{backgroundColor:'grey'}}
                                                    onClick={()=> {
                                                    dayClick(a);
                                                    
                                                }}>
                                                    {a.getDate()}
                                                 </td>
                                    }else{
                                        return <td className='col-md-1 item-tab' key={a} onClick={()=> {
                                            dayClick(a);
                                            dayClickColor(a);
                                        }}>{a.getDate()}</td>
                                    }
                                    
                                })} 
                                </tr>
                        </tbody>
                    </Table>
                </div>





                <div className='col-md-6'>

                <>
      <Button variant="primary" onClick={handleShow}>
        Take Appointment
      </Button>

      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Modal heading</Modal.Title>
        </Modal.Header>
        <Modal.Body>
        <Form.Group className="mb-3" controlId="formRDV_titre">
                            <Form.Control className="text-center" type="text" placeholder="Enter le titre du RDV" name="titre" onChange={onChange}/>
                        </Form.Group>
                        <input type="textarea" 
                                name="commentaire"
                                placeholder="Enter votre commentaire"
                                style={{width:'30em'}}
                                onChange={onChange}
                                className='text-center'
                        />

<Form.Group className="mb-3 mt-3" controlId="formRDV_date">
                            <Form.Control className="text-center" type="text" placeholder="Choisissez votre date" name="date" disabled={true} value={selectedDay}/>
                        </Form.Group>
                       

        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          <Button variant="success" type="submit" onClick={(e)=> prendreRdv(e)}>
                            Confirm RDV
                        </Button>
        </Modal.Footer>
      </Modal>
    </>
                   
                </div>
            </div>









            
            <div className="row text-center mt-5">
                <h4>Les RDV</h4>
                {rdvDuJourChoisi.length>0 && 
                    rdvDuJourChoisi.map(rdv => { 
                        <>
     

      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>{rdv.titre}</Modal.Title>
        </Modal.Header>
        <Modal.Body>{rdv.commentaire}</Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          <Button variant="primary" onClick={handleClose}>
            Save Changes
          </Button>
        </Modal.Footer>
      </Modal>
    </> 
                        
    
                    })
                }
            </div>
              
            
      
    </div>
  )
}
